INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23843, NULL, 354, 'Для научных исследований в области медицины очень сложная процедура сертификации. Также многое сырье закупается за рубежом. Это касается не только медицинской сферы, но затрагивает и другие технологические сферы.', 1528370520, 3, '', 1, 'Дмитрий Сосновский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23845, NULL, 354, 'Есть регионы, в которых проблема обманутых дольщиков успешно решается. Но есть и негативный опыт. Например, есть соответствующие сообщения из Липецка и Владивостока. ', 1528370700, 3, '', 1, 'Дмитрий Сосновский');
