INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23936, NULL, 354, 'Ирина, пациент Военно-медицинской академии имени Кирова, Санкт-Петербург: Спасибо, что обратили внимание на мою просьбу. Также огромное спасибо Сергею Шойгу и военным медикам. Я хочу вернуться к нормальной жизни, для этого мне нужны нормальные протезы. Но их выдают только гражданам РФ. Очень прошу вас предоставить мне гражданство. Также не могу воссоединиться с семьей - я гражданка Украины, они граждане Сирии.', 1528377240, 3, '', 1, 'Илья Максимов');
