INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23951, NULL, 354, 'Спасибо большое всем участникам этой встречи. Я знаю, что заданных вопросов гораздо больше, чем тех, на которые я смог ответить. Но хочу заверить, что мы проанализируем поступившие вопросы, сделаем это по максимуму, чтобы обобщить вашу озабоченность, чтобы поставить задачи для правительства на следующие годы. Поступило такое обращение: "Страна вопросы задает, а я хочу вас поддержать в этот день". Всем, кто так думает, спасибо.', 1528377960, 2, '', 0, 'Дмитрий Сосновский');
