INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23966, NULL, 355, 'Добрый вечер, уважаемые любители спорта! Начинаем нашу трансляцию. Сегодня нас ждет очень интересный вечер бокса!', 1532196000, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23967, NULL, 355, 'До главного боя сегодняшнего вечера времени еще предостаточно. До финала WBSS в "Олимпийском" состоятся еще три интересных поединка. В первом россиянин Федор Чудинов сразится с французом Наджибом Мохаммеди. Это второй средний вес.', 1532196540, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23968, NULL, 355, 'Это титульный бой - за пояс WBA International. Сейчас чемпионом является наш боксер, который провел на профессиональном ринге 19 боев, 17 из которых выиграл. Мохаммеди - гораздо опытнее. Ему 33 года, но в активе 45 боев и 40 побед.', 1532196900, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23969, NULL, 355, 'Несколько предварительных боев уже состоялось.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23970, NULL, 355, 'Между тем к месту событий подъехал наш корреспондент Илья Соболев. И встретил здесь немало персональных болельщиков Мурата Гассиева. "Такое ощущение, что к "Олимпийскому" съехалась вся осетинская диаспора Москвы", - сообщает Илья.', 1532197680, 1, '', 1, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23971, NULL, 355, 'Фанаты, впрочем, будут и у украинского боксера, свидетельствует другой снимок Ильи Соболева.', 1532197860, 1, '', 1, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23972, NULL, 355, 'Украинцы, судя по всему, чувствуют себя в Москве в полной безопасности. Александр Усик - тем более, несмотря на все разговоры вокруг этого боя. 
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23978, NULL, 355, 'Федор Чудинов назван судьями победителем в бою с Мохаммеди, но зал встречает это решение свистом! Действительно, явно неудачный поединок провел российский боксер, и его победа стала неожиданностью, наверное, даже для него самого. "Домашнее судейство", - так в подобных случаях говорят в футболе.', 1532199780, 1, '', 0, 'Андрей Полухин');
