INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23990, NULL, 355, 'Стартовал последний бой андеркарта - латвиец Майрис Бриедис проэкзаменует француза Брэндона Делорье. Судя по первыми минутам - исход предрешен. Бывший чемпион мира готовился биться с Гассиевым или Усиком, а потому готов к матчу прекрасно.', 1532203500, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24068, NULL, 356, 'Колесико Digital Crown получило тактильную отдачу, а динамик стал громче.', 1536772560, 1, '', 0, 'Антон Благовещенский');
