INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24003, NULL, 355, 'Сегодня на трибунах в "Олимпийском" десятки прославленных боксеров, но, возможно, главный гость турнира - последняя жена легендарного Мохаммеда Али - Лонни Али. Именно она вручит Кубок Али - Ali Trophi - победителю. Это прекрасное дополнение к четырем чемпионским поясам.', 1532206380, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24004, NULL, 355, 'И снова фото из "Олимпийского" от Ильи Соболева. "Парни ждут "отбить пять" Гассиеву", - пишет он про мальчиков с перчатками.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24005, NULL, 355, 'Ажиотаж нарастает! 
