INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24033, NULL, 355, '"Мы еще вернемся и станем еще сильнее", - сказал сейчас Гассиев во флеш-интервью. Почему мы? Может, вместе с Усиком они вернутся в "Олимпийский" и проведут еще один блестящий бой-реванш?', 1532211960, 1, '', 0, 'Андрей Полухин');
