INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24049, NULL, 356, 'Напомним между тем, что на новые айфоны уже принимаются предварительные заказы от серых поставщиков и частных лиц, готовых слетать в Гонконг.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24050, NULL, 356, 'Тим Кук проснулся и готовит всех к "большому дню" в своем твиттере.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24052, NULL, 356, 'Ну что же, объявляется практически часовая готовность. Все затаили дыхание.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24053, NULL, 356, 'В зал, где состоится презентация, пустили людей. Осталось совсем немного.', 1536771720, 1, '', 0, 'Антон Благовещенский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24054, NULL, 356, 'Началось! После небольшого ролика, героем которого стали Тим Кук и переключатель демонстрационных слайдов, на сцену вышел сам Тим Кук.', 1536771900, 1, '', 0, 'Антон Благовещенский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24055, NULL, 356, 'Рассказывает о двух миллиардах проданных iOS-девайсов.', 1536771960, 1, '', 0, 'Антон Благовещенский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24056, NULL, 356, 'Говорит, что девайсы меняют все. То, как мы делаем покупки, учимся, обмениваемся фотками.', 1536772020, 1, '', 0, 'Антон Благовещенский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24057, NULL, 356, 'Начался рассказ об Apple Watch, которые стали самыми популярными умными часами в мире. Ими пользуются "миллионы людей".', 1536772080, 1, '', 0, 'Антон Благовещенский');
