INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24070, NULL, 356, 'А еще новые Apple Watch понимают, если их хозяин упал.', 1536772680, 1, '', 0, 'Антон Благовещенский');
